plot(kk, sum(AERR7, 2)/sample, '--o', 'LineWidth', 2, 'MarkerSize', 10);hold on
plot(kk, sum(AERR3, 2)/sample, '--d', 'LineWidth', 2, 'MarkerSize', 10);hold on
