subplot(122)
plot(kk, sum(AT5, 2)/sample, '-+', 'LineWidth', 2, 'MarkerSize', 10);hold on
plot(kk, sum(AT1, 2)/sample, '-o', 'LineWidth', 2, 'MarkerSize', 10);hold on
plot(kk, sum(AT6, 2)/sample, ':*', 'LineWidth', 2, 'MarkerSize', 10);hold on
plot(kk, sum(AT2, 2)/sample, ':+', 'LineWidth', 2, 'MarkerSize', 10);hold on
plot(kk, sum(AT7, 2)/sample, '--o', 'LineWidth', 2, 'MarkerSize', 10);hold on
plot(kk, sum(AT3, 2)/sample, '--d', 'LineWidth', 2, 'MarkerSize', 10);hold on
plot(kk, sum(AT8, 2)/sample, '-.*', 'LineWidth', 2, 'MarkerSize', 10);hold on
plot(kk, sum(AT4, 2)/sample, '-.s', 'LineWidth', 2, 'MarkerSize', 10);hold on
figure(3)
subplot(121)
semilogy(kk, sum(AERR5, 2)/sample, '-+', 'LineWidth', 2, 'MarkerSize', 10);hold on
semilogy(kk, sum(AERR1, 2)/sample, '-o', 'LineWidth', 2, 'MarkerSize', 10);hold on
semilogy(kk, sum(AERR6, 2)/sample, ':*', 'LineWidth', 2, 'MarkerSize', 10);hold on
semilogy(kk, sum(AERR2, 2)/sample, ':+', 'LineWidth', 2, 'MarkerSize', 10);hold on
semilogy(kk, sum(AERR7, 2)/sample, '--o', 'LineWidth', 2, 'MarkerSize', 10);hold on
semilogy(kk, sum(AERR3, 2)/sample, '--d', 'LineWidth', 2, 'MarkerSize', 10);hold on
semilogy(kk, sum(AERR8, 2)/sample, '-.*', 'LineWidth', 2, 'MarkerSize', 10);hold on
semilogy(kk, sum(AERR4, 2)/sample, '-.s', 'LineWidth', 2, 'MarkerSize', 10);hold on
subplot(122)
semilogy(kk, sum(AT5, 2)/sample, '-+', 'LineWidth', 2, 'MarkerSize', 10);hold on
semilogy(kk, sum(AT1, 2)/sample, '-o', 'LineWidth', 2, 'MarkerSize', 10);hold on
semilogy(kk, sum(AT6, 2)/sample, ':*', 'LineWidth', 2, 'MarkerSize', 10);hold on
semilogy(kk, sum(AT2, 2)/sample, ':+', 'LineWidth', 2, 'MarkerSize', 10);hold on
